var rollspeed=50;var myInter;function MarqueeV(){var e=document.getElementById("oRollV");var t=document.getElementById("oRollV1");var n=document.getElementById("oRollV2");if(n.offsetTop-e.scrollTop<=0){e.scrollTop-=t.offsetHeight}else{e.scrollTop++}}function StartRollV(){var e=document.getElementById("oRollV");var t=document.getElementById("oRollV1");var n=document.getElementById("oRollV2");if(e){if(parseInt(e.style.height,10)>=n.offsetTop){e.style.height=n.offsetTop;return}n.innerHTML=t.innerHTML;myInter=setInterval(MarqueeV,rollspeed);e.onmouseover=function(){clearInterval(myInter)};e.onmouseout=function(){myInter=setInterval(MarqueeV,rollspeed)}}}